tables.table_schema:
CREATE TABLE neural_network_weights (
    weight_id UUID PRIMARY KEY,
    weight_name VARCHAR(100),
    weight_value FLOAT
);

CREATE TABLE model_parameters (
    parameter_id UUID PRIMARY KEY,
    parameter_name VARCHAR(100),
    parameter_value FLOAT
);

CREATE TABLE neural_network_weights (
    layer_id INT,
    weight FLOAT,
    bias FLOAT
);

CREATE TABLE neural_network_parameters (
    parameter_id INT,
    parameter_name VARCHAR(50),
    parameter_value FLOAT
);

CREATE TABLE model_deployment (
    model_id INT,
    deployment_status VARCHAR(50),
    deployment_date DATE
);

CREATE TABLE NeuralNetworkModel (
  id INT PRIMARY KEY,
  model_name STRING,
  architecture STRING,
  hyperparameters STRING
);

CREATE TABLE LayerWeights (
  layer_id INT,
  weight_matrix BLOB,
  CONSTRAINT fk_layer_nnmodel FOREIGN KEY (layer_id) REFERENCES NeuralNetworkModel(id)
);

CREATE TABLE ActivationFunctions (
  func_id INT PRIMARY KEY,
  function_name STRING
);
