tables.table_schema:
CREATE TABLE KnowledgeNodes (
  node_id INT PRIMARY KEY,
  node_label VARCHAR(255),
  node_properties JSONB
);

CREATE TABLE KnowledgeGraph (
  graph_id INT PRIMARY KEY,
  graph_name VARCHAR(255),
  created_date TIMESTAMP,
  graph_properties JSONB
);

CREATE TABLE KnowledgeNodes (
    node_id SERIAL PRIMARY KEY,
    node_name VARCHAR(255) NOT NULL,
    node_type VARCHAR(50) NOT NULL
);

CREATE TABLE KnowledgeAttributes (
    attribute_id SERIAL PRIMARY KEY,
    attribute_name VARCHAR(255) NOT NULL,
    attribute_value TEXT NOT NULL,
    node_id INT NOT NULL
);

CREATE TABLE KnowledgeGraph (
    graph_id SERIAL PRIMARY KEY,
    graph_name VARCHAR(255) NOT NULL,
    description TEXT
);

CREATE TABLE knowledge_nodes (
    node_id UUID PRIMARY KEY,
    node_label VARCHAR(255),
    node_type VARCHAR(50)
);

CREATE TABLE knowledge_edges (
    edge_id UUID PRIMARY KEY,
    source_node_id UUID,
    target_node_id UUID,
    relation_label VARCHAR(255)
);

CREATE TABLE knowledge_properties (
    property_id UUID PRIMARY KEY,
    node_id UUID,
    property_name VARCHAR(255),
    property_value TEXT
);

CREATE TABLE knowledge_graphs (
    graph_id UUID PRIMARY KEY,
    graph_name VARCHAR(255),
    graph_description TEXT
);

CREATE TABLE knowledge_queries (
    query_id UUID PRIMARY KEY,
    query_text TEXT,
    query_result TEXT
);
