tables.table_schema:
CREATE TABLE water_conservation_technologies (
    technology_id SERIAL PRIMARY KEY,
    technology_name VARCHAR(50) NOT NULL,
    description TEXT,
    impact_on_reduction DECIMAL
);

CREATE TABLE technologies (
  technology_id INT PRIMARY KEY,
  technology_name VARCHAR(50),
  description TEXT,
  usage_reduction DECIMAL(5,2)
);

CREATE TABLE organizations (
  org_id INT PRIMARY KEY,
  org_name VARCHAR(50),
  location VARCHAR(100),
  contact_person VARCHAR(50)
);

CREATE TABLE Water_Saving_Technologies (
    technology_id INT PRIMARY KEY,
    technology_name VARCHAR(50),
    description TEXT,
    water_savings FLOAT
);

CREATE TABLE WaterTech (
    tech_id SERIAL PRIMARY KEY,
    tech_name VARCHAR(50) NOT NULL,
    description TEXT,
    usage_reduction DECIMAL
);

CREATE TABLE Organization (
    org_id SERIAL PRIMARY KEY,
    org_name VARCHAR(50) NOT NULL,
    location VARCHAR(100),
    contact_email VARCHAR(50)
);

CREATE TABLE TechOrganization (
    tech_org_id SERIAL PRIMARY KEY,
    tech_id INT REFERENCES WaterTech(tech_id),
    org_id INT REFERENCES Organization(org_id)
);

CREATE TABLE ImpactMetrics (
    metric_id SERIAL PRIMARY KEY,
    tech_id INT REFERENCES WaterTech(tech_id),
    impact_measure VARCHAR(50),
    value DECIMAL
);

CREATE TABLE WaterConservationOrganization (
    id INT PRIMARY KEY,
    organization_name VARCHAR(255),
    contact_email VARCHAR(255),
    location VARCHAR(255)
);

CREATE TABLE WaterSavingTechnologies (
    id INT PRIMARY KEY,
    technology_name VARCHAR(255),
    description TEXT,
    organization_id INT REFERENCES WaterConservationOrganization(id)
);

CREATE TABLE UsageReduction (
    id INT PRIMARY KEY,
    reduction_date DATE,
    gallons_saved DECIMAL,
    technology_id INT REFERENCES WaterSavingTechnologies(id)
);

CREATE TABLE OrganizationsStats (
    id INT PRIMARY KEY,
    organization_id INT REFERENCES WaterConservationOrganization(id),
    total_gallons_saved DECIMAL,
    total_technologies_used INT
);
