tables.table_schema:
CREATE TABLE water_usage (
	id INT PRIMARY KEY,
	location_id INT,
	date DATE,
	usage_amount DECIMAL(10, 2)
);

CREATE TABLE local_governments (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    location VARCHAR(100) NOT NULL
);

CREATE TABLE WaterAuthorities (
    authority_id INT PRIMARY KEY,
    authority_name VARCHAR(50) NOT NULL,
    region VARCHAR(50) NOT NULL
);

CREATE TABLE WaterRestrictions (
    restriction_id INT PRIMARY KEY,
    restriction_name VARCHAR(50) NOT NULL,
    restriction_level INT NOT NULL,
    region VARCHAR(50) NOT NULL
);
