tables.table_schema:
CREATE TABLE water_demand_forecast (
    id INT PRIMARY KEY,
    usage_pattern INT,
    population_growth INT
);

CREATE TABLE water_demand_forecast (
  id INT PRIMARY KEY,
  company_id INT,
  usage_pattern JSON,
  population_growth DECIMAL
);

CREATE TABLE population_data (
  id INT PRIMARY KEY,
  region VARCHAR(50),
  year INT,
  population INT
);

CREATE TABLE WaterDemandForecast (
    location_id INT PRIMARY KEY,
    usage_date DATE,
    usage_volume FLOAT,
    population INT
);

CREATE TABLE PopulationGrowth (
    location_id INT PRIMARY KEY,
    year INT,
    population_count INT
);

CREATE TABLE HistoricalUsage (
    location_id INT,
    usage_date DATE,
    usage_volume FLOAT,
    PRIMARY KEY (location_id, usage_date)
);

CREATE TABLE WaterUsage (
  usage_id SERIAL PRIMARY KEY,
  meter_id INT REFERENCES WaterMeter(meter_id),
  usage_date DATE,
  gallons_used INT
);
