tables.table_schema:
CREATE TABLE Researchers (
	researcher_id UUID PRIMARY KEY,
	researcher_name VARCHAR(255) NOT NULL,
	email VARCHAR(255) UNIQUE,
	institution VARCHAR(255)
);

CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(50),
    research_topic VARCHAR(100)
);

CREATE TABLE weather_conditions (
    weather_id INT PRIMARY KEY,
    date DATE,
    temperature FLOAT,
    precipitation FLOAT
);

CREATE TABLE solar_energy_generation (
    generation_id INT PRIMARY KEY,
    date DATE,
    energy_output FLOAT,
    efficiency FLOAT
);

CREATE TABLE research_data (
    research_id INT PRIMARY KEY,
    researcher_id INT,
    generation_id INT,
    weather_id INT
);

CREATE TABLE research_results (
    result_id INT PRIMARY KEY,
    research_id INT,
    conclusion TEXT
);
