tables.table_schema:
CREATE TABLE WasteDisposalData (
  disposal_date TIMESTAMP,
  location VARCHAR,
  waste_type VARCHAR,
  quantity DECIMAL
);

CREATE TABLE Waste_Disposal_Trends (
  trend_id UUID PRIMARY KEY,
  trend_date DATE,
  trend_analysis TEXT,
  trend_impact VARCHAR(100)
);

CREATE TABLE Waste_Management_Strategies (
  strategy_id UUID PRIMARY KEY,
  strategy_name VARCHAR(100),
  strategy_description TEXT,
  strategy_owner VARCHAR(100)
);

CREATE TABLE waste_management_strategies (
  strategy_id INT PRIMARY KEY,
  strategy_name VARCHAR(255),
  implementation_date DATE,
  target_reduction DECIMAL(5,2)
);
