tables.table_schema:
CREATE TABLE disposal_containers (
  container_id UUID PRIMARY KEY,
  location VARCHAR(255),
  capacity INT,
  current_level INT
);

CREATE TABLE container_logs (
    id INT PRIMARY KEY,
    container_id INT,
    log_date DATE,
    log_description TEXT
);

CREATE TABLE Waste_Containers (
    container_id INT PRIMARY KEY,
    container_name VARCHAR(255) NOT NULL,
    capacity INT NOT NULL,
    location VARCHAR(255) NOT NULL
);

CREATE TABLE Restocking_Logs (
    log_id INT PRIMARY KEY,
    container_id INT NOT NULL,
    restock_date DATE NOT NULL,
    restock_quantity INT NOT NULL
);

CREATE TABLE Container_Inventory (
    inventory_id INT PRIMARY KEY,
    container_id INT NOT NULL,
    quantity INT NOT NULL,
    last_restock_date DATE NOT NULL
);

CREATE TABLE Waste_Collection (
    collection_id INT PRIMARY KEY,
    container_id INT NOT NULL,
    collection_date DATE NOT NULL,
    collected_quantity INT NOT NULL
);
