tables.table_schema:
CREATE TABLE waste_disposal (
   id INT PRIMARY KEY,
   waste_type VARCHAR(255),
   weight DECIMAL(10,2),
   disposal_date DATE
);

CREATE TABLE billing_information (
   id INT PRIMARY KEY,
   customer_id INT,
   amount DECIMAL(10,2),
   invoice_date DATE
);

CREATE TABLE customers (
   id INT PRIMARY KEY,
   name VARCHAR(255),
   address VARCHAR(255),
   phone_number VARCHAR(15)
);
