tables.table_schema:
CREATE TABLE waste_diversion_metrics (
    waste_diversion_id INT PRIMARY KEY,
    program_name VARCHAR(50) NOT NULL,
    total_waste_collected DECIMAL,
    recycling_rate DECIMAL
);

CREATE TABLE waste_diversion_metrics (
  id SERIAL PRIMARY KEY,
  date DATE NOT NULL,
  materials_type VARCHAR(50) NOT NULL,
  weight_in_kg FLOAT NOT NULL
);

CREATE TABLE recycling_programs (
  id SERIAL PRIMARY KEY,
  program_name VARCHAR(100) NOT NULL,
  start_date DATE NOT NULL,
  end_date DATE NOT NULL
);

CREATE TABLE measurement_results (
  id SERIAL PRIMARY KEY,
  program_id INT NOT NULL,
  date DATE NOT NULL,
  diversion_rate DECIMAL(5,2) NOT NULL
);

CREATE TABLE waste_diversion_metrics (
  id INT PRIMARY KEY,
  program_name VARCHAR(255),
  recycling_rate FLOAT
);

CREATE TABLE collection_points (
  id INT PRIMARY KEY,
  location VARCHAR(255),
  type VARCHAR(50),
  capacity INT
);

CREATE TABLE collection_records (
  id INT PRIMARY KEY,
  collection_date DATE,
  collected_quantity FLOAT,
  collection_point_id INT
);

CREATE TABLE recycling_materials (
  id INT PRIMARY KEY,
  material_name VARCHAR(100),
  recycling_type VARCHAR(50)
);

CREATE TABLE program_participants (
  id INT PRIMARY KEY,
  participant_name VARCHAR(255),
  program_id INT
);
