tables.table_schema:
CREATE TABLE waste_treatment_facilities (facility_id INT PRIMARY KEY,
facility_name VARCHAR(50) NOT NULL,
capacity DECIMAL(10,2) NOT NULL
);

CREATE TABLE waste_management_plan (plan_id INT PRIMARY KEY,
facility_id INT NOT NULL,
plan_date DATE NOT NULL,
plan_details TEXT
);

CREATE TABLE facilities (
    facility_id SERIAL PRIMARY KEY,
    facility_name VARCHAR(50) NOT NULL,
    capacity DECIMAL NOT NULL
);

CREATE TABLE waste_types (
    waste_type_id SERIAL PRIMARY KEY,
    waste_type_name VARCHAR(50) NOT NULL
);

CREATE TABLE facility_waste_capacity (
    facility_id INT REFERENCES facilities(facility_id),
    waste_type_id INT REFERENCES waste_types(waste_type_id),
    capacity DECIMAL NOT NULL,
    PRIMARY KEY (facility_id, waste_type_id)
);

CREATE TABLE waste_facilities (
    facility_id INT PRIMARY KEY,
    facility_name VARCHAR(50) NOT NULL,
    location VARCHAR(100) NOT NULL,
    capacity INT NOT NULL
);

CREATE TABLE waste_types (
    type_id INT PRIMARY KEY,
    waste_type VARCHAR(50) NOT NULL
);

CREATE TABLE WasteFacilities (
    facility_id INT PRIMARY KEY,
    facility_name TEXT,
    location TEXT,
    capacity DECIMAL
);

CREATE TABLE WasteMaterials (
    material_id INT PRIMARY KEY,
    material_type TEXT,
    material_source TEXT,
    disposal_method TEXT
);
