tables.table_schema:
CREATE TABLE greenhouse_gas_sources (
    source_id INT PRIMARY KEY,
    source_name VARCHAR(255),
    location VARCHAR(255),
    emissions FLOAT
);

CREATE TABLE emissions_records (
    record_id INT PRIMARY KEY,
    source_id INT,
    record_date DATE,
    emissions_amount FLOAT,
    CONSTRAINT fk_source FOREIGN KEY (source_id) REFERENCES greenhouse_gas_sources(source_id)
);

CREATE TABLE greenhouse_sources (
    source_id INT PRIMARY KEY,
    source_name VARCHAR(255) UNIQUE,
    emission_type VARCHAR(50),
    emission_amount DECIMAL(10, 2)
);

CREATE TABLE emission_categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(100) UNIQUE,
    description TEXT
);

CREATE TABLE GreenhouseGasEmissions (
  source_id INT PRIMARY KEY,
  source_name VARCHAR(255) NOT NULL,
  emission_type VARCHAR(50) NOT NULL,
  emission_quantity DECIMAL(10,2) NOT NULL
);

CREATE TABLE EmissionSources (
  source_id INT PRIMARY KEY,
  source_name VARCHAR(255) NOT NULL,
  source_type VARCHAR(50) NOT NULL
);

CREATE TABLE EmissionCategories (
  category_id INT PRIMARY KEY,
  category_name VARCHAR(100) NOT NULL
);

CREATE TABLE Greenhouse_Gas_Emission_Sources (
    source_id INT PRIMARY KEY,
    source_name VARCHAR(100) NOT NULL,
    emission_type VARCHAR(50) NOT NULL,
    emission_level DECIMAL(10,2) NOT NULL
);

CREATE TABLE Emission_Categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(100) NOT NULL
);
