tables.table_schema:
CREATE TABLE climate_data (
  location VARCHAR(50),
  temperature FLOAT,
  humidity FLOAT,
  deforestation_rate FLOAT
);

CREATE TABLE dashboard_settings (
  dashboard_id INT,
  user_id INT,
  theme VARCHAR(20),
  widget_settings JSONB
);

CREATE TABLE deforestation_data (
  country VARCHAR(50),
  forest_type VARCHAR(50),
  deforestation_rate FLOAT,
  climate_impact VARCHAR(100)
);

CREATE TABLE climate_data (
  month VARCHAR(10),
  temperature FLOAT,
  precipitation FLOAT,
  climate_event VARCHAR(50)
);

CREATE TABLE dashboard_visualization (
  date DATE,
  country VARCHAR(50),
  dashboard_type VARCHAR(50),
  visualization_link TEXT
);

CREATE TABLE DeforestationCauses (
    cause_id INT,
    cause_name VARCHAR(255),
    primary_key(cause_id)
);

CREATE TABLE DashboardUsers (
    user_id INT,
    user_name VARCHAR(255),
    subscription_status BOOLEAN,
    primary_key(user_id)
);

CREATE TABLE deforestation_impact (
    country_id INT PRIMARY KEY,
    country_name VARCHAR(255),
    forest_area_km2 FLOAT,
    deforestation_rate FLOAT
);

CREATE TABLE climate_data (
    year INT PRIMARY KEY,
    temperature_celsius FLOAT,
    humidity_percentage FLOAT,
    carbon_dioxide_ppm FLOAT
);

CREATE TABLE impact_metrics (
    metric_id INT PRIMARY KEY,
    metric_name VARCHAR(255),
    impact_value FLOAT
);

CREATE TABLE dashboard_users (
    user_id INT PRIMARY KEY,
    username VARCHAR(255),
    email VARCHAR(255)
);

CREATE TABLE dashboard_logs (
    log_id INT PRIMARY KEY,
    user_id INT,
    timestamp TIMESTAMP,
    action_taken VARCHAR(255)
);
