tables.table_schema:
CREATE TABLE weather_patterns (
    id INT PRIMARY KEY,
    date DATE,
    temperature FLOAT,
    precipitation FLOAT
);

CREATE TABLE climate_trends (
    id INT PRIMARY KEY,
    region VARCHAR,
    trend VARCHAR
);

CREATE TABLE future_scenarios (
    id INT PRIMARY KEY,
    scenario VARCHAR,
    prediction TEXT
);

CREATE TABLE climate_patterns (
    pattern_id INT PRIMARY KEY,
    pattern_description TEXT,
    pattern_start_date DATE,
    pattern_end_date DATE
);
