tables.table_schema:
CREATE TABLE SolarPower (
	solar_id INT PRIMARY KEY,
	solar_location TEXT,
	solar_capacity INT
);

CREATE TABLE WindPower (
	wind_id INT PRIMARY KEY,
	wind_location TEXT,
	wind_capacity INT
);

CREATE TABLE Solar_Panels (
    id INT PRIMARY KEY,
    brand VARCHAR(50),
    capacity FLOAT,
    efficiency FLOAT
);

CREATE TABLE Wind_Turbines (
    id INT PRIMARY KEY,
    brand VARCHAR(50),
    rotor_diameter FLOAT,
    rated_power FLOAT
);

CREATE TABLE Energy_Plants (
    id INT PRIMARY KEY,
    plant_name VARCHAR(50),
    location VARCHAR(100),
    type VARCHAR(50)
);

CREATE TABLE SolarPanels (
    id INT PRIMARY KEY,
    panel_name VARCHAR(50),
    capacity FLOAT,
    efficiency FLOAT
);

CREATE TABLE WindTurbines (
    id INT PRIMARY KEY,
    turbine_name VARCHAR(50),
    height FLOAT,
    blade_length FLOAT
);

CREATE TABLE EnergyProduction (
    id INT PRIMARY KEY,
    production_date DATE,
    energy_type VARCHAR(50),
    energy_production FLOAT
);

CREATE TABLE EnergyConsumption (
    id INT PRIMARY KEY,
    consumption_date DATE,
    consumer_name VARCHAR(50),
    energy_consumption FLOAT
);

CREATE TABLE solar_panels (
  panel_id INT PRIMARY KEY,
  panel_location VARCHAR(255) NOT NULL,
  panel_capacity FLOAT NOT NULL
);

CREATE TABLE wind_turbines (
  turbine_id INT PRIMARY KEY,
  turbine_location VARCHAR(255) NOT NULL,
  turbine_capacity FLOAT NOT NULL
);

CREATE TABLE energy_consumption (
  consumption_id SERIAL PRIMARY KEY,
  date DATE NOT NULL,
  energy_consumed FLOAT NOT NULL
);
