tables.table_schema:
CREATE TABLE hurricanes (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    location VARCHAR(100) NOT NULL,
    date DATE NOT NULL
);

CREATE TABLE floods (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    severity VARCHAR(20) NOT NULL,
    affected_area VARCHAR(100) NOT NULL
);

CREATE TABLE wildfires (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    region VARCHAR(50) NOT NULL,
    acres_burned DECIMAL(10,2) NOT NULL
);

CREATE TABLE response_planning (
    id INT PRIMARY KEY,
    disaster_type VARCHAR(20) NOT NULL,
    action_plan TEXT NOT NULL
);
