tables.table_schema:
CREATE TABLE Animals (
    animal_id SERIAL PRIMARY KEY,
    species VARCHAR(50) NOT NULL,
    migration_status VARCHAR(50) NOT NULL,
    affected_by_climate_change BOOLEAN
);

CREATE TABLE MigrationEvents (
    event_id SERIAL PRIMARY KEY,
    animal_id INT REFERENCES Animals(animal_id),
    event_date DATE NOT NULL,
    event_description TEXT
);

CREATE TABLE AnimalMigration (
    animal_id INT PRIMARY KEY,
    animal_name VARCHAR(255),
    migration_date DATE,
    migration_destination VARCHAR(255)
);

CREATE TABLE ClimateData (
    climate_id INT PRIMARY KEY,
    temperature FLOAT,
    humidity FLOAT,
    weather_condition VARCHAR(255)
);

CREATE TABLE MigrationRoute (
    route_id INT PRIMARY KEY,
    animal_id INT,
    climate_id INT,
    migration_distance FLOAT
);

CREATE TABLE Animal (
    animal_id SERIAL PRIMARY KEY,
    species VARCHAR(50) NOT NULL,
    migration_pattern VARCHAR(100) NOT NULL
);

CREATE TABLE Location (
    location_id SERIAL PRIMARY KEY,
    location_name VARCHAR(100) NOT NULL,
    latitude FLOAT NOT NULL,
    longitude FLOAT NOT NULL
);

CREATE TABLE locations (
  location_id SERIAL PRIMARY KEY,
  location_name VARCHAR(100) NOT NULL,
  latitude DECIMAL NOT NULL,
  longitude DECIMAL NOT NULL
);

CREATE TABLE sightings (
  sighting_id SERIAL PRIMARY KEY,
  animal_id INT NOT NULL,
  location_id INT NOT NULL,
  sighting_date DATE NOT NULL
);

CREATE TABLE migration_routes (
  route_id SERIAL PRIMARY KEY,
  animal_id INT NOT NULL,
  route_description TEXT NOT NULL
);
