tables.table_schema:
CREATE TABLE UrbanDevelopmentImpact (
  city_name VARCHAR(255),
  population INT,
  carbon_emissions DECIMAL,
  infrastructure_rating INT
);

CREATE TABLE UrbanDevelopmentImpact (
  city_id INT PRIMARY KEY,
  city_name VARCHAR(255),
  temperature DECIMAL,
  population INT
);

CREATE TABLE InfrastructureImpact (
  infra_id INT PRIMARY KEY,
  infra_type VARCHAR(100),
  carbon_emission DECIMAL,
  energy_consumption DECIMAL
);

CREATE TABLE city_data (
    city_id INT PRIMARY KEY,
    city_name VARCHAR(50),
    population INT,
    avg_temperature FLOAT
);

CREATE TABLE infrastructure_data (
    infrastructure_id INT PRIMARY KEY,
    infrastructure_name VARCHAR(50),
    city_id INT,
    construction_year INT
);

CREATE TABLE emissions_data (
    emission_id INT PRIMARY KEY,
    city_id INT,
    year INT,
    co2_emissions FLOAT
);
