tables.table_schema:
CREATE TABLE PolicyOrganizations (
    id INT PRIMARY KEY,
    organization_name VARCHAR(255),
    headquarters_location VARCHAR(255),
    contact_email VARCHAR(255)
);

CREATE TABLE PolicyChanges (
    id INT PRIMARY KEY,
    policy_id INT,
    change_date DATE,
    change_description TEXT
);

CREATE TABLE PolicyRatings (
    id INT PRIMARY KEY,
    policy_id INT,
    rating FLOAT,
    reviewer_name VARCHAR(255)
);
