tables.table_schema:
CREATE TABLE Biodiversity (
  species_id INT PRIMARY KEY,
  species_name VARCHAR(255),
  habitat_type VARCHAR(255),
  conservation_status VARCHAR(50)
);

CREATE TABLE CropPollination (
  crop_id INT PRIMARY KEY,
  crop_name VARCHAR(255),
  pollinator_id INT,
  pollinator_name VARCHAR(255)
);

CREATE TABLE biodiversity (
  id INT PRIMARY KEY,
  scientific_name VARCHAR(255),
  common_name VARCHAR(255),
  description TEXT
);

CREATE TABLE habitat_conservation (
  id INT PRIMARY KEY,
  habitat_type VARCHAR(255),
  conservation_status VARCHAR(255),
  description TEXT
);

CREATE TABLE crop_pollination (
  id INT PRIMARY KEY,
  crop_name VARCHAR(255),
  pollinator_type VARCHAR(255),
  pollination_method TEXT
);

CREATE TABLE biodiversity_data (
  id INT PRIMARY KEY,
  species_name VARCHAR(255) NOT NULL,
  population_size INT,
  habitat_type VARCHAR(50)
);

CREATE TABLE habitat_conservation (
  id INT PRIMARY KEY,
  habitat_id INT NOT NULL,
  conservation_status VARCHAR(50),
  conservation_actions TEXT
);

CREATE TABLE crop_pollination (
  id INT PRIMARY KEY,
  crop_name VARCHAR(100) NOT NULL,
  pollinator_species VARCHAR(255),
  pollination_method VARCHAR(50)
);

CREATE TABLE farming_practices (
  id INT PRIMARY KEY,
  practice_name VARCHAR(100) NOT NULL,
  sustainability_rating INT,
  implementation_date DATE
);
