tables.table_schema:
CREATE TABLE ProductRegistrations (
  registration_id INT PRIMARY KEY,
  product_name VARCHAR(255),
  registration_date DATE
);

CREATE TABLE products (
  id INT PRIMARY KEY,
  name TEXT,
  registration_date DATE
);

CREATE TABLE application_rates (
  id INT PRIMARY KEY,
  product_id INT REFERENCES products(id),
  application_rate DECIMAL,
  application_date DATE
);

CREATE TABLE residue_levels (
  id INT PRIMARY KEY,
  product_id INT REFERENCES products(id),
  residue_level DECIMAL,
  test_date DATE
);

CREATE TABLE product_registrations (
    registration_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    company_name VARCHAR(255),
    registration_date DATE
);

CREATE TABLE companies (
    company_id INT PRIMARY KEY,
    company_name VARCHAR(255),
    country VARCHAR(50),
    contact_person VARCHAR(100)
);

CREATE TABLE ProductRegistrations (
    registration_id UUID PRIMARY KEY,
    product_name VARCHAR(255),
    manufacturer VARCHAR(255),
    registration_date DATE
);

CREATE TABLE ApplicationRates (
    application_id UUID PRIMARY KEY,
    registration_id UUID REFERENCES ProductRegistrations(registration_id),
    application_date DATE,
    application_rate FLOAT
);

CREATE TABLE ResidueLevels (
    residue_id UUID PRIMARY KEY,
    registration_id UUID REFERENCES ProductRegistrations(registration_id),
    residue_date DATE,
    residue_level FLOAT
);

CREATE TABLE Companies (
    company_id UUID PRIMARY KEY,
    company_name VARCHAR(255),
    location VARCHAR(255)
);

CREATE TABLE CompanyProducts (
    company_id UUID REFERENCES Companies(company_id),
    registration_id UUID REFERENCES ProductRegistrations(registration_id),
    PRIMARY KEY (company_id, registration_id)
);
