tables.table_schema:
CREATE TABLE Retailers (
    retailer_id UUID PRIMARY KEY,
    retailer_name STRING,
    location STRING,
    supply_chain_traceability BOOLEAN
);

CREATE TABLE supply_chain_traceability (
  product_id INT PRIMARY KEY,
  product_name VARCHAR(255),
  certification VARCHAR(50),
  manufacturer VARCHAR(100)
);

CREATE TABLE product_certifications (
  certification_id INT PRIMARY KEY,
  certification_name VARCHAR(100),
  certification_body VARCHAR(100)
);

CREATE TABLE sustainable_sourcing_practices (
  source_id INT PRIMARY KEY,
  source_name VARCHAR(100),
  certification_status BOOLEAN,
  organic_certification VARCHAR(50)
);

CREATE TABLE Certifications (
    certification_id INT PRIMARY KEY,
    certification_type VARCHAR(50) NOT NULL,
    certification_date DATE
);

CREATE TABLE Retailers (
    retailer_id INT PRIMARY KEY,
    retailer_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL
);

CREATE TABLE Suppliers (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL
);

CREATE TABLE Sustainable_Practices (
    practice_id INT PRIMARY KEY,
    practice_name VARCHAR(255) NOT NULL,
    description TEXT
);
