tables.table_schema:
CREATE TABLE energy_usage (
    farm_id INT NOT NULL,
    energy_type VARCHAR(255) NOT NULL,
    energy_amount DECIMAL(10,2) NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE renewable_energy (
    id INT PRIMARY KEY,
    source VARCHAR,
    amount DECIMAL,
    date DATE
);

CREATE TABLE waste_management (
    id INT PRIMARY KEY,
    type VARCHAR,
    weight DECIMAL,
    disposal_method VARCHAR
);

CREATE TABLE carbon_footprint (
    id INT PRIMARY KEY,
    operation VARCHAR,
    emissions DECIMAL,
    date DATE
);

CREATE TABLE Energy_Usage (
    id INT PRIMARY KEY,
    farm_id INT,
    energy_source VARCHAR(255),
    energy_amount DECIMAL(10,2)
);

CREATE TABLE Waste_Management (
    id INT PRIMARY KEY,
    farm_id INT,
    waste_type VARCHAR(255),
    waste_amount DECIMAL(10,2)
);

CREATE TABLE Carbon_Footprint (
    id INT PRIMARY KEY,
    farm_id INT,
    footprint_category VARCHAR(255),
    footprint_measure DECIMAL(10,2)
);

CREATE TABLE Farm_Operations (
    id INT PRIMARY KEY,
    farm_name VARCHAR(255),
    location VARCHAR(255),
    establishment_date DATE
);

CREATE TABLE Farm_Details (
    id INT PRIMARY KEY,
    farm_id INT,
    farm_size DECIMAL(10,2),
    crop_type VARCHAR(255)
);
