tables.table_schema:
CREATE TABLE SolarPanels (
	solar_panel_id INT PRIMARY KEY,
	location VARCHAR(50),
	production_date DATE,
	production_output FLOAT
);

CREATE TABLE WindTurbines (
	turbine_id INT PRIMARY KEY,
	location VARCHAR(50),
	production_date DATE,
	production_output FLOAT
);

CREATE TABLE SolarPanelPerformance (
	solar_panel_id INT,
	performance_date DATE,
	performance_metric FLOAT,
	FOREIGN KEY (solar_panel_id) REFERENCES SolarPanels(solar_panel_id)
);

CREATE TABLE WindTurbinePerformance (
	turbine_id INT,
	performance_date DATE,
	performance_metric FLOAT,
	FOREIGN KEY (turbine_id) REFERENCES WindTurbines(turbine_id)
);

CREATE TABLE WeatherData (
	location VARCHAR(50),
	weather_date DATE,
	temperature FLOAT,
	wind_speed FLOAT
);
