tables.table_schema:
CREATE TABLE offsetting_activities (
   id INT PRIMARY KEY,
   activity_name TEXT,
   activity_type TEXT,
   offsetting_cost DECIMAL
);

CREATE TABLE emission_offsets (
   id INT PRIMARY KEY,
   emission_id INT,
   activity_id INT,
   offset_amount DECIMAL
);

CREATE TABLE carbon_emissions (
  id INT PRIMARY KEY,
  location VARCHAR,
  emission_value FLOAT,
  date_recorded DATE
);

CREATE TABLE carbon_emissions (
   company_id INT PRIMARY KEY,
   emission_date DATE,
   emitted_tons FLOAT
);

CREATE TABLE companies (
   company_id INT PRIMARY KEY,
   company_name VARCHAR(50),
   company_size VARCHAR(10)
);

CREATE TABLE carbon_emissions (
  id SERIAL PRIMARY KEY,
  emission_source VARCHAR(100) NOT NULL,
  emission_amount DECIMAL NOT NULL,
  emission_date DATE
);

CREATE TABLE offsetting_activities (
  id SERIAL PRIMARY KEY,
  activity_name VARCHAR(100) NOT NULL,
  activity_type VARCHAR(50) NOT NULL,
  activity_date DATE
);

CREATE TABLE emissions_offset (
  id SERIAL PRIMARY KEY,
  emission_id INT REFERENCES carbon_emissions(id) ON DELETE CASCADE,
  activity_id INT REFERENCES offsetting_activities(id) ON DELETE CASCADE
);

CREATE TABLE emission_reports (
  id SERIAL PRIMARY KEY,
  report_title VARCHAR(200) NOT NULL,
  report_description TEXT,
  report_date DATE
);

CREATE TABLE Users (
  id INT PRIMARY KEY,
  username VARCHAR(255),
  email VARCHAR(255),
  join_date DATE
);
