tables.table_schema:
CREATE TABLE WaterConservationEfforts (
        project_id INT PRIMARY KEY,
        project_name VARCHAR(255),
        project_location VARCHAR(255),
        water_saved_gallons INT
    );

CREATE TABLE GreenTechnologyProjects (
        project_id INT PRIMARY KEY,
        technology_name VARCHAR(255),
        technology_type VARCHAR(255),
        project_description TEXT
    );
