tables.table_schema:
CREATE TABLE crop_yield_data(
    crop_id INT PRIMARY KEY,
    crop_name VARCHAR(255),
    yield_amount FLOAT,
    harvest_date DATE
);

CREATE TABLE Crop_Yields (
 id INT PRIMARY KEY,
 crop_name VARCHAR(255),
 yield_amount FLOAT,
 yield_date DATE
);

CREATE TABLE Agricultural_Practices (
 id INT PRIMARY KEY,
 practice_name VARCHAR(255),
 practice_description TEXT
);

CREATE TABLE sustainability_practices (
  practice_id INT PRIMARY KEY,
  practice_name VARCHAR(255),
  description TEXT
);

CREATE TABLE CropData (
  crop_id INT PRIMARY KEY,
  crop_name VARCHAR(255) NOT NULL,
  yield_amount DECIMAL NOT NULL,
  harvest_date DATE
);

CREATE TABLE FieldData (
  field_id INT PRIMARY KEY,
  field_name VARCHAR(255) NOT NULL,
  location VARCHAR(255) NOT NULL,
  size DECIMAL NOT NULL
);

CREATE TABLE FarmerData (
  farmer_id INT PRIMARY KEY,
  farmer_name VARCHAR(255) NOT NULL,
  contact_info VARCHAR(255) NOT NULL
);

CREATE TABLE FarmData (
  farm_id INT PRIMARY KEY,
  farm_name VARCHAR(255) NOT NULL,
  location VARCHAR(255) NOT NULL
);

CREATE TABLE Crop_Yield (
    crop_id INT PRIMARY KEY,
    crop_name TEXT,
    yield_value FLOAT
);

CREATE TABLE Field (
    field_id INT PRIMARY KEY,
    field_name TEXT
);

CREATE TABLE Farmer (
    farmer_id INT PRIMARY KEY,
    farmer_name TEXT
);

CREATE TABLE Agricultural_Practices (
    practice_id INT PRIMARY KEY,
