tables.table_schema:
CREATE TABLE carbon_emissions (
  country VARCHAR(255),
  renewable_energy_source VARCHAR(255),
  carbon_emissions_reduction FLOAT
);

CREATE TABLE emissions (
 id INT,
 date DATE,
 renewable_source VARCHAR,
 emission_reduction DECIMAL
);

CREATE TABLE sources (
 id INT,
 source_name VARCHAR,
 source_type VARCHAR,
 capacity DECIMAL
);

CREATE TABLE carbon_emissions (
  emission_id INT PRIMARY KEY,
  company_name VARCHAR(50),
  emission_date DATE,
  emission_amount DECIMAL
);

CREATE TABLE renewable_energy_sources (
  source_id INT PRIMARY KEY,
  source_name VARCHAR(50) UNIQUE,
  source_type VARCHAR(50),
  source_capacity DECIMAL
);

CREATE TABLE carbon_emission_tracking(
   id INT PRIMARY KEY,
   company_name VARCHAR(255),
   energy_source VARCHAR(255),
   emission_reduction_percentage DECIMAL(5, 2)
);

CREATE TABLE renewable_energy_sources(
   id INT PRIMARY KEY,
   source_name VARCHAR(255),
   energy_type VARCHAR(255),
   capacity_kW INT
);

CREATE TABLE companies(
   id INT PRIMARY KEY,
   company_name VARCHAR(255),
   industry VARCHAR(255),
   country VARCHAR(255)
);

CREATE TABLE renewable_energy_sources (
    id SERIAL PRIMARY KEY,
    source_name TEXT NOT NULL,
    carbon_reduction DECIMAL NOT NULL
);

CREATE TABLE companies (
    id SERIAL PRIMARY KEY,
    company_name TEXT NOT NULL,
    country TEXT NOT NULL
);

CREATE TABLE projects (
    id SERIAL PRIMARY KEY,
    project_name TEXT NOT NULL,
    start_date DATE NOT NULL,
    end_date DATE
);

CREATE TABLE emissions (
    id SERIAL PRIMARY KEY,
    project_id INT REFERENCES projects(id),
    company_id INT REFERENCES companies(id),
    emission_value DECIMAL NOT NULL
);

CREATE TABLE emissions_reductions (
    id SERIAL PRIMARY KEY,
    emission_id INT REFERENCES emissions(id),
    reduction_amount DECIMAL NOT NULL
);
