tables.table_schema:
CREATE TABLE endangered_species (
  species_id INT PRIMARY KEY,
  species_name STRING,
  population_trend STRING
);

CREATE TABLE population_data (
  data_id INT PRIMARY KEY,
  species_id INT REFERENCES endangered_species(species_id),
  year INT,
  population_size INT
);

CREATE TABLE endangered_species (
    species_id INT PRIMARY KEY,
    species_name VARCHAR(50),
    population_count INT,
    conservation_status VARCHAR(50)
);

CREATE TABLE population_trends (
    trend_id INT PRIMARY KEY,
    species_id INT REFERENCES endangered_species(species_id),
    year INT,
    population_change INT
);

CREATE TABLE species_habitat (
    habitat_id INT PRIMARY KEY,
    species_id INT REFERENCES endangered_species(species_id),
    habitat_type VARCHAR(50),
    habitat_description TEXT
);

CREATE TABLE endangered_species (
  species_id INT PRIMARY KEY,
  species_name VARCHAR(255),
  population INTEGER,
  conservation_status VARCHAR(50)
);

CREATE TABLE population_trends (
  trend_id INT PRIMARY KEY,
  species_id INT,
  year INTEGER,
  population_count INTEGER
);

CREATE TABLE conservation_actions (
  action_id INT PRIMARY KEY,
  species_id INT,
  action_name VARCHAR(255),
  action_date DATE
);

CREATE TABLE habitat_data (
  habitat_id INT PRIMARY KEY,
  species_id INT,
  habitat_type VARCHAR(100),
  habitat_area DECIMAL(10,2)
);

CREATE TABLE EndangeredSpecies (
    species_id INT PRIMARY KEY,
    species_name VARCHAR(255),
    population_size INT,
    conservation_status VARCHAR(50)
);

CREATE TABLE SpeciesClassification (
    classification_id INT PRIMARY KEY,
    species_id INT,
    kingdom VARCHAR(50),
    phylum VARCHAR(50),
    class VARCHAR(50),
    CONSTRAINT fk_species_id FOREIGN KEY (species_id) REFERENCES EndangeredSpecies(species_id)
);
