tables.table_schema:
CREATE TABLE birds (
   bird_id INT PRIMARY KEY,
   bird_name VARCHAR(50),
   location_name VARCHAR(50),
   migration_date DATE
);

CREATE TABLE locations (
   location_id INT PRIMARY KEY,
   location_name VARCHAR(50),
   latitude FLOAT,
   longitude FLOAT
);

CREATE TABLE BirdLocations (
  bird_id INT PRIMARY KEY,
  location VARCHAR(255) NOT NULL,
  timestamp TIMESTAMP NOT NULL
);

CREATE TABLE BirdSpecies (
  species_id INT PRIMARY KEY,
  species_name VARCHAR(255) NOT NULL,
  average_wingspan DECIMAL(5,2) NOT NULL
);

CREATE TABLE BirdSpecies (
   species_id INT PRIMARY KEY,
   species_name TEXT
);

CREATE TABLE BirdWatchers (
   watcher_id INT PRIMARY KEY,
   watcher_name TEXT,
   region TEXT
);

CREATE TABLE bird_species (
  species_id INT PRIMARY KEY,
  species_name VARCHAR(50) NOT NULL
);

CREATE TABLE tracking_devices (
  device_id INT PRIMARY KEY,
  device_name VARCHAR(50) NOT NULL
);
