CREATE TABLE conservation (
    conservation_id INT PRIMARY KEY,
    species_id INT,
    area_id INT,
    conservation_status VARCHAR(20)
);

CREATE TABLE areas (
  area_id INT PRIMARY KEY,
  area_name VARCHAR(255),
  biodiversity_score FLOAT
);

CREATE TABLE species (
  species_id INT PRIMARY KEY,
  species_name VARCHAR(255),
  habitat_type VARCHAR(100),
  endangered BOOLEAN
);

CREATE TABLE conservation_plans (
  plan_id INT PRIMARY KEY,
  area_id INT,
  conservation_objective TEXT,
  target_species_id INT
);

CREATE TABLE biodiversity_areas (
  area_id INT PRIMARY KEY,
  area_name TEXT NOT NULL,
  biodiversity_index FLOAT
);

CREATE TABLE species (
  species_id INT PRIMARY KEY,
  species_name TEXT NOT NULL,
  conservation_status TEXT
);

CREATE TABLE area_species (
  area_id INT,
  species_id INT,
  FOREIGN KEY (area_id) REFERENCES biodiversity_areas(area_id),
  FOREIGN KEY (species_id) REFERENCES species(species_id)
);

CREATE TABLE threats (
  threat_id INT PRIMARY KEY,
  threat_name TEXT NOT NULL
);

CREATE TABLE area_threats (
  area_id INT,
  threat_id INT,
  FOREIGN KEY (area_id) REFERENCES biodiversity_areas(area_id),
  FOREIGN KEY (threat_id) REFERENCES threats(threat_id)
);
