tables.table_schema:
CREATE TABLE invasive_species (
  species_id UUID PRIMARY KEY,
  name VARCHAR(255) NOT NULL,
  scientific_name VARCHAR(255) NOT NULL,
  invasive_since DATE NOT NULL
);

CREATE TABLE ecosystem_impact (
  impact_id UUID PRIMARY KEY,
  species_id UUID REFERENCES invasive_species(species_id),
  impact_description TEXT NOT NULL,
  impact_date DATE
);

CREATE TABLE InvasiveSpecies (
  species_id INT PRIMARY KEY,
  species_name VARCHAR(50) NOT NULL,
  impact_description TEXT
);

CREATE TABLE Ecosystems (
  ecosystem_id INT PRIMARY KEY,
  ecosystem_name VARCHAR(50) NOT NULL,
  location VARCHAR(100)
);

CREATE TABLE invasive_species (id INT PRIMARY KEY, name VARCHAR(255), scientific_name VARCHAR(255), origin VARCHAR(255), impact_on_ecosystem TEXT);

CREATE TABLE ecosystems (id INT PRIMARY KEY, name VARCHAR(255), location VARCHAR(255), description TEXT);

CREATE TABLE species_population (id INT PRIMARY KEY, species_id INT, ecosystem_id INT, population_size INT, last_recorded_date DATE);

CREATE TABLE research_studies (id INT PRIMARY KEY, study_id INT, researcher_name VARCHAR(255), study_topic VARCHAR(255), publication_date DATE);

CREATE TABLE invasive_species (
   species_id INT PRIMARY KEY,
   species_name VARCHAR(50) NOT NULL,
   impact_description TEXT
);

CREATE TABLE ecosystems (
   ecosystem_id INT PRIMARY KEY,
   ecosystem_name VARCHAR(50) NOT NULL,
   location VARCHAR(100)
);

CREATE TABLE species_ecosystems (
   species_id INT,
   ecosystem_id INT,
   FOREIGN KEY (species_id) REFERENCES invasive_species(species_id),
   FOREIGN KEY (ecosystem_id) REFERENCES ecosystems(ecosystem_id)
);

CREATE TABLE research_studies (
   study_id INT PRIMARY KEY,
   study_name VARCHAR(100) NOT NULL,
   researcher_name VARCHAR(50),
   findings TEXT
);
