tables.table_schema:
CREATE TABLE habitat_loss (
  region_id INT PRIMARY KEY,
  area_lost DECIMAL,
  time_period INT,
  population_density DECIMAL
);

CREATE TABLE habitat_loss_rates (
  region_id INT PRIMARY KEY,
  habitat_loss_rate DECIMAL
);

CREATE Table HabitatLossData (
  region_id INT,
  year INT,
  habitat_area_km2 FLOAT,
  PRIMARY KEY(region_id, year)
);

CREATE Table HabitatGrowthData (
  region_id INT,
  year INT,
  habitat_area_km2 FLOAT,
  PRIMARY KEY(region_id, year)
);

CREATE Table HabitatLossRate (
  region_id INT,
  year INT,
  loss_rate DECIMAL,
  PRIMARY KEY(region_id, year)
);

CREATE TABLE habitat_loss (
  region_id UUID PRIMARY KEY,
  habitat_type TEXT,
  area_km2 FLOAT,
  year INT
);

CREATE TABLE region_info (
  region_id UUID PRIMARY KEY,
  region_name TEXT,
  country TEXT,
  land_area_km2 FLOAT
);

CREATE TABLE habitat_loss_rates (
    region_id INT PRIMARY KEY,
    region_name TEXT,
    habitat_area_km2 FLOAT,
    deforestation_rate FLOAT
);

CREATE TABLE deforestation_reasons (
    reason_id INT PRIMARY KEY,
    reason_name TEXT
);

CREATE TABLE deforestation_data (
    data_id INT PRIMARY KEY,
    region_id INT,
    reason_id INT,
    deforestation_area_km2 FLOAT,
    year INT
);

CREATE TABLE deforestation_trends (
    trend_id INT PRIMARY KEY,
    region_id INT,
    deforestation_percent_change FLOAT,
    trend_year INT
);

CREATE TABLE habitat_repopulation (
    repopulation_id INT PRIMARY KEY,
    region_id INT,
    repopulation_rate FLOAT,
    year INT
);
