tables.table_schema:
CREATE TABLE species_population ( 
  species_id UUID PRIMARY KEY, 
  species_name VARCHAR(50) NOT NULL, 
  population_count INTEGER NOT NULL, 
  last_updated TIMESTAMP 
);

CREATE TABLE Species (
    id INT PRIMARY KEY,
    species_name VARCHAR(255) NOT NULL,
    population INT
);

CREATE TABLE Population_Records (
    record_id INT PRIMARY KEY,
    species_id INT,
    population_count INT,
    record_date DATE,
    FOREIGN KEY (species_id) REFERENCES Species(id)
);

CREATE TABLE species_population (
    species_name VARCHAR(50),
    population_count INT,
    last_updated TIMESTAMP
);

CREATE TABLE location (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(50),
    latitude FLOAT,
    longitude FLOAT
);

CREATE TABLE population_update (
    update_id INT PRIMARY KEY,
    species_name VARCHAR(50),
    new_population_count INT,
    update_date TIMESTAMP
);

CREATE TABLE Species (
    species_id INT PRIMARY KEY,
    species_name VARCHAR(255) NOT NULL,
    population_size INT,
    date_updated DATE
);

CREATE TABLE PopulationRecord (
    record_id INT PRIMARY KEY,
    species_id INT NOT NULL,
    population_amount INT,
    observation_date DATE,
    FOREIGN KEY (species_id) REFERENCES Species(species_id)
);

CREATE TABLE SpeciesCategory (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(255) NOT NULL
);

CREATE TABLE SpeciesCategoryMap (
    map_id INT PRIMARY KEY,
    species_id INT NOT NULL,
    category_id INT NOT NULL,
    FOREIGN KEY (species_id) REFERENCES Species(species_id),
    FOREIGN KEY (category_id) REFERENCES SpeciesCategory(category_id)
);
