tables.table_schema:
CREATE TABLE conservation_effectiveness_measure (
    measure_id INT PRIMARY KEY,
    measure_name VARCHAR(50),
    before_data INT,
    after_data INT
);

CREATE TABLE ConservationBefore ( 
    id INT PRIMARY KEY, 
    measure_name VARCHAR(50), 
    effectiveness_rating INT 
);

CREATE TABLE ConservationAfter ( 
    id INT PRIMARY KEY, 
    measure_name VARCHAR(50), 
    effectiveness_rating INT 
);

CREATE TABLE ConservationMeasuresBefore (
    id INT PRIMARY KEY,
    measure_name VARCHAR(50),
    effectiveness_score FLOAT
);

CREATE TABLE ConservationMeasuresAfter (
    id INT PRIMARY KEY,
    measure_name VARCHAR(50),
    post_effects_score FLOAT
);

CREATE TABLE ConservationMeasureComparison (
    id INT PRIMARY KEY,
    measure_name VARCHAR(50),
    before_effectiveness_score FLOAT,
    after_effectiveness_score FLOAT
);

CREATE TABLE conservation_measure_before (
    id INT PRIMARY KEY,
    measure_name VARCHAR(255),
    effectiveness_score INT
);

CREATE TABLE conservation_measure_after (
    id INT PRIMARY KEY,
    measure_name VARCHAR(255),
    effectiveness_score INT
);

CREATE TABLE conservation_measure_impact (
    id INT PRIMARY KEY,
    measure_name VARCHAR(255),
    impact_description TEXT
);

CREATE TABLE conservation_measure_comparison (
    id INT PRIMARY KEY,
    measure_id INT,
    before_effectiveness_score INT,
    after_effectiveness_score INT
);
