tables.table_schema:
CREATE TABLE WildlifeHabitats (
	habitat_id INT PRIMARY KEY,
	habitat_name VARCHAR(50) NOT NULL,
	habitat_location GEOGRAPHY(POINT) NOT NULL
);

CREATE TABLE EcosystemConnectivity (
	connectivity_id INT PRIMARY KEY,
	habitat_source_id INT REFERENCES WildlifeHabitats(habitat_id) NOT NULL,
	habitat_destination_id INT REFERENCES WildlifeHabitats(habitat_id) NOT NULL,
	connectivity_score FLOAT
);

CREATE TABLE Wildlife_Habitats (
    habitat_id INT PRIMARY KEY,
    habitat_name VARCHAR(255),
    habitat_location GEOGRAPHY
);

CREATE TABLE Ecosystems (
    ecosystem_id INT PRIMARY KEY,
    ecosystem_name VARCHAR(255),
    ecosystem_type VARCHAR(50)
);

CREATE TABLE habitats (
id INT PRIMARY KEY,
name VARCHAR(255) NOT NULL,
area DECIMAL(10, 2) NOT NULL
);

CREATE TABLE ecosystems (
id INT PRIMARY KEY,
name VARCHAR(255) NOT NULL,
region VARCHAR(50) NOT NULL
);

CREATE TABLE wildlife (
id INT PRIMARY KEY,
name VARCHAR(255) NOT NULL,
habitat_id INT,
FOREIGN KEY (habitat_id) REFERENCES habitats(id)
);

CREATE TABLE connectivity (
id INT PRIMARY KEY,
wildlife_id INT,
ecosystem_id INT,
FOREIGN KEY (wildlife_id) REFERENCES wildlife(id),
FOREIGN KEY (ecosystem_id) REFERENCES ecosystems(id)
);

CREATE TABLE habitats (
  habitat_id INT PRIMARY KEY,
  habitat_name VARCHAR(255),
  habitat_type VARCHAR(100),
  habitat_location GEOMETRY
);

CREATE TABLE ecosystems (
  ecosystem_id INT PRIMARY KEY,
  ecosystem_name VARCHAR(255),
  ecosystem_type VARCHAR(100)
);
