tables.table_schema:
CREATE TABLE WindFarms (
    id INT PRIMARY KEY,
    location VARCHAR(255),
    wind_speed DECIMAL,
    wind_direction VARCHAR(50)
);

CREATE TABLE LocationData (
    location_id INT PRIMARY KEY,
    latitude FLOAT,
    longitude FLOAT
);

CREATE TABLE WindSpeedData (
    wind_speed_id INT PRIMARY KEY,
    location_id INT,
    wind_speed FLOAT,
    FOREIGN KEY (location_id) REFERENCES LocationData(location_id)
);

CREATE TABLE WindDirectionData (
    wind_direction_id INT PRIMARY KEY,
    location_id INT,
    wind_direction VARCHAR(255),
    FOREIGN KEY (location_id) REFERENCES LocationData(location_id)
);

CREATE TABLE wind_farm_locations (
    id SERIAL PRIMARY KEY,
    location_name VARCHAR(100),
    latitude DECIMAL(9, 6),
    longitude DECIMAL(9, 6)
);

CREATE TABLE wind_turbines (
    id SERIAL PRIMARY KEY,
    turbine_model VARCHAR(50),
    manufacturer VARCHAR(50),
    blade_length DECIMAL(5, 2)
);

CREATE TABLE turbine_installations (
    id SERIAL PRIMARY KEY,
    turbine_id INTEGER REFERENCES wind_turbines(id),
    location_id INTEGER REFERENCES wind_farm_locations(id),
    installation_date DATE
);

CREATE TABLE wind_farm_rating (
    id SERIAL PRIMARY KEY,
    location_id INTEGER REFERENCES wind_farm_locations(id),
    efficiency_rating DECIMAL(4, 2),
    evaluation_date DATE
);
