tables.table_schema:
CREATE TABLE soil_samples (
   sample_id INT PRIMARY KEY,
   sample_location VARCHAR(100),
   contamination_level DECIMAL
);

CREATE TABLE SoilSamples (
  sample_id INT PRIMARY KEY,
  location_geometry GEOMETRY,
  contamination_level FLOAT
);

CREATE TABLE SamplingSites (
  site_id INT PRIMARY KEY,
  site_name VARCHAR(50),
  site_location GEOGRAPHY
);

CREATE TABLE Contaminants (
  contaminant_id INT PRIMARY KEY,
  contaminant_name VARCHAR(50),
  health_effect VARCHAR(100)
);

CREATE TABLE AnalysisResults (
  result_id INT PRIMARY KEY,
  sample_id INT REFERENCES SoilSamples(sample_id),
  contaminant_id INT REFERENCES Contaminants(contaminant_id),
  concentration FLOAT
);

CREATE TABLE Maps (
  map_id INT PRIMARY KEY,
  map_name VARCHAR(50),
  map_image BYTEA,
  coverage_area GEOMETRY
);
