tables.table_schema:
CREATE TABLE impact_of_industrial_activities (
  id INT PRIMARY KEY,
  location VARCHAR(255),
  activity_type VARCHAR(255),
  impact_description TEXT
);

CREATE TABLE ecosystems_impact (
  id INT PRIMARY KEY,
  industrial_activity TEXT,
  ecosystem_name TEXT,
  impact_description TEXT
);

CREATE TABLE impact_assessment (
    id INT PRIMARY KEY,
    activity_name VARCHAR(255),
    ecosystem_type VARCHAR(255),
    impact_description TEXT
);

CREATE TABLE biodiversity_data (
    id INT PRIMARY KEY,
    species_name VARCHAR(255),
    population_count INT,
    habitat_type VARCHAR(255)
);

CREATE TABLE industrial_activities (
    id INT PRIMARY KEY,
    company_name VARCHAR(255),
    activity_type VARCHAR(255),
    location VARCHAR(255)
);

CREATE TABLE environmental_changes (
    id INT PRIMARY KEY,
    change_type VARCHAR(255),
    change_description TEXT,
    affected_area VARCHAR(255)
);
