tables.table_schema:
CREATE TABLE climate_data (
    year INT,
    month INT,
    temperature FLOAT,
    precipitation FLOAT
);

CREATE TABLE environmental_predictions (
    prediction_id INT,
    prediction_text TEXT,
    prediction_date DATE
);

CREATE TABLE climate_patterns (
    date DATE,
    temperature FLOAT,
    humidity FLOAT
);

CREATE TABLE environmental_trends (
    trend_id INT,
    trend_description VARCHAR,
    predicted_impact VARCHAR
);

CREATE TABLE data_sources (
    source_id INT,
    source_name VARCHAR,
    source_type VARCHAR
);

CREATE TABLE ClimateData ( 
   date DATE, 
   temperature FLOAT, 
   humidity FLOAT, 
   precipitation FLOAT 
 );

CREATE TABLE WeatherForecast ( 
   forecast_date DATE, 
   predicted_temperature FLOAT, 
   predicted_wind_speed FLOAT, 
   chance_of_rain FLOAT 
 );

CREATE TABLE EnvironmentalFactors ( 
   environment_id INT, 
   factor_description STRING, 
   factor_value FLOAT 
 );

CREATE TABLE HistoricalTrends ( 
   trend_id INT, 
   trend_description STRING, 
   trend_value FLOAT 
 );

CREATE TABLE PredictedTrends ( 
   prediction_id INT, 
   prediction_description STRING, 
   predicted_value FLOAT 
 );
