tables.table_schema:
CREATE TABLE SolarPanels (
	solar_panel_id INT PRIMARY KEY,
	property_id INT,
	solar_panel_brand VARCHAR(255),
	installation_date DATE
);

CREATE TABLE ResidentialProperties (
	property_id INT PRIMARY KEY,
	homeowner_name VARCHAR(255),
	property_address VARCHAR(255),
	property_size_sqft INT
);

CREATE TABLE CostBenefitAnalysis (
	analysis_id INT PRIMARY KEY,
	property_id INT,
	total_cost DECIMAL(10,2),
	estimated_savings DECIMAL(10,2)
);

CREATE TABLE EnergyConsultants (
	consultant_id INT PRIMARY KEY,
	consultant_name VARCHAR(255),
	consultant_specialty VARCHAR(255)
);

CREATE TABLE IF NOT EXISTS EnergyConsultants (
  consultant_id UUID PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  email VARCHAR(50) NOT NULL,
  phone_number VARCHAR(20) NOT NULL
);

CREATE TABLE IF NOT EXISTS SolarPanels (
  panel_id UUID PRIMARY KEY,
  manufacturer VARCHAR(50) NOT NULL,
  wattage FLOAT NOT NULL,
  efficiency FLOAT NOT NULL
);
