tables.table_schema:
CREATE TABLE marine_regions ( 
 region_id UUID PRIMARY KEY, 
 region_name VARCHAR(100) NOT NULL 
 );

CREATE TABLE pollution_levels ( 
 pollution_id UUID PRIMARY KEY, 
 region_id UUID REFERENCES marine_regions(region_id), 
 pollution_type VARCHAR(50) NOT NULL, 
 pollution_level INT NOT NULL 
 );

CREATE TABLE Marine_Regions (
    region_id INT PRIMARY KEY,
    region_name VARCHAR(50) NOT NULL
);

CREATE TABLE Pollution_Causes (
    cause_id INT PRIMARY KEY,
    cause_description TEXT NOT NULL
);

CREATE TABLE regions (
 region_id INT PRIMARY KEY,
 region_name VARCHAR(50) NOT NULL
);

CREATE TABLE pollution_levels (
 pollution_id INT PRIMARY KEY,
 pollution_level INT NOT NULL,
 region_id INT REFERENCES regions(region_id)
);

CREATE TABLE pollutants (
 pollutant_id INT PRIMARY KEY,
 pollutant_name VARCHAR(50) NOT NULL
);

CREATE TABLE pollutant_data (
 data_id INT PRIMARY KEY,
 pollution_id INT REFERENCES pollution_levels(pollution_id),
 pollutant_id INT REFERENCES pollutants(pollutant_id),
 data_value FLOAT NOT NULL
);

CREATE TABLE Region (
  region_id INT PRIMARY KEY,
  region_name VARCHAR(50) NOT NULL
);

CREATE TABLE PollutionLevel (
  pollution_id INT PRIMARY KEY,
  pollution_level VARCHAR(50) NOT NULL,
  region_id INT REFERENCES Region(region_id)
);

CREATE TABLE PollutionSource (
  source_id INT PRIMARY KEY,
  source_name VARCHAR(50) NOT NULL
);

CREATE TABLE Measurements (
  measurement_id INT PRIMARY KEY,
  measurement_value FLOAT NOT NULL,
  pollution_id INT REFERENCES PollutionLevel(pollution_id),
  source_id INT REFERENCES PollutionSource(source_id)
);

CREATE TABLE RegionData (
  region_data_id INT PRIMARY KEY,
  region_id INT REFERENCES Region(region_id),
  measurement_id INT REFERENCES Measurements(measurement_id)
);
