tables.table_schema:
CREATE TABLE ConservationAreas (
    area_id SERIAL PRIMARY KEY,
    area_name VARCHAR(50) NOT NULL
);

CREATE TABLE EndangeredSpecies (
    species_id SERIAL PRIMARY KEY,
    species_name VARCHAR(50) NOT NULL,
    population_count INT NOT NULL,
    conservation_area_id INT REFERENCES ConservationAreas(area_id)
);

CREATE TABLE ConservationAreas (
  area_id INT PRIMARY KEY,
  area_name VARCHAR(50) NOT NULL,
  area_location VARCHAR(100) NOT NULL
);

CREATE TABLE MarineSpecies (
  species_id INT PRIMARY KEY,
  species_name VARCHAR(50) NOT NULL,
  species_description TEXT
);

CREATE TABLE endangered_species (
    species_id SERIAL PRIMARY KEY,
    species_name VARCHAR(50) NOT NULL,
    conservation_area VARCHAR(100) NOT NULL,
    population_size INT NOT NULL
);

CREATE TABLE conservation_areas (
    area_id SERIAL PRIMARY KEY,
    area_name VARCHAR(100) NOT NULL,
    location VARCHAR(150) NOT NULL
);

CREATE TABLE researchers (
    researcher_id SERIAL PRIMARY KEY,
    researcher_name VARCHAR(50) NOT NULL,
    specialty VARCHAR(50) NOT NULL
);

CREATE TABLE conservation_projects (
    project_id SERIAL PRIMARY KEY,
    project_name VARCHAR(100) NOT NULL,
    start_date DATE NOT NULL,
    end_date DATE NOT NULL
);
