tables.table_schema:
CREATE TABLE marine_protected_areas (
  id INT PRIMARY KEY,
  location VARCHAR(255),
  size DECIMAL,
  regulations TEXT
);

CREATE TABLE marine_protected_areas (
  id UUID PRIMARY KEY,
  location VARCHAR(255) NOT NULL,
  size DECIMAL NOT NULL,
  regulations VARCHAR(255)
);

CREATE TABLE marine_species (
  id UUID PRIMARY KEY,
  species_name VARCHAR(255) NOT NULL,
  common_name VARCHAR(255),
  habitat VARCHAR(255)
);

CREATE TABLE marine_users (
  id UUID PRIMARY KEY,
  username VARCHAR(255) NOT NULL,
  email VARCHAR(255) UNIQUE,
  role VARCHAR(50) NOT NULL
);

CREATE TABLE marine_protected_areas (
  area_id INT PRIMARY KEY,
  location VARCHAR(255),
  size FLOAT,
  regulations VARCHAR(255)
);

CREATE TABLE MarineRegulations (
    regulation_id INT PRIMARY KEY,
    regulation_name VARCHAR(50),
    description TEXT
);
