    total_amount FLOAT,
    start_date DATE,
    end_date DATE
);

CREATE TABLE Fisherman (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    age INT,
    address VARCHAR(100)
);

CREATE TABLE fishing_vessels (
   vessel_id INT PRIMARY KEY,
   vessel_name VARCHAR(50) NOT NULL,
   registration_date DATE,
   fishing_license_number VARCHAR(20) UNIQUE
);

CREATE TABLE fishing_quotas (
   quota_id INT PRIMARY KEY,
   quota_species VARCHAR(50) NOT NULL,
   quota_limit DECIMAL(10,2) NOT NULL
);

CREATE TABLE Fishing_Zones (
  zone_id UUID PRIMARY KEY,
  zone_name VARCHAR(50) NOT NULL,
  country VARCHAR(50) NOT NULL
);

CREATE TABLE Fishing_Vessels (
  vessel_id UUID PRIMARY KEY,
  vessel_name VARCHAR(100) NOT NULL,
  vessel_type VARCHAR(50)
);

CREATE TABLE Fishing_Quotas (
  quota_id UUID PRIMARY KEY,
  zone_id UUID REFERENCES Fishing_Zones(zone_id),
  quota_amount INT
);

CREATE TABLE Fishing_CatchData (
  catch_id UUID PRIMARY KEY,
  log_id UUID REFERENCES Fishing_Logs(log_id),
  species VARCHAR(50) NOT NULL,
  weight DECIMAL(10,2) NOT NULL
);
