tables.table_schema:
CREATE TABLE Ocean_Temperature (
    measurement_id SERIAL PRIMARY KEY,
    date DATE NOT NULL,
    temperature FLOAT NOT NULL
);

CREATE TABLE Marine_Ecosystem (
    ecosystem_id SERIAL PRIMARY KEY,
    ecosystem_name TEXT NOT NULL
);

CREATE TABLE Climate_Impacts (
    impact_id SERIAL PRIMARY KEY,
    measurement_id INT NOT NULL,
    ecosystem_id INT NOT NULL,
    impact_description TEXT
);

CREATE TABLE ocean_regions (
    region_id INT PRIMARY KEY,
    region_name VARCHAR(50) NOT NULL
);

CREATE TABLE marine_species (
    species_id INT PRIMARY KEY,
    species_name VARCHAR(50) NOT NULL,
    common_name VARCHAR(50),
    average_lifespan INT
);
