tables.table_schema:
CREATE TABLE coastal_erosion_rates (
  id INT PRIMARY KEY,
  location VARCHAR(255),
  erosion_rate DECIMAL(10, 2)
);

CREATE TABLE coastal_protection_measures (
  id INT PRIMARY KEY,
  measure_name VARCHAR(255),
  measure_type VARCHAR(50),
  implementation_date DATE
);

CREATE TABLE coastal_erosion (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(50),
    erosion_rate FLOAT,
    measurement_date DATE
);

CREATE TABLE protective_measures (
    measure_id INT PRIMARY KEY,
    measure_name VARCHAR(50),
    cost DECIMAL,
    implementation_date DATE
);

CREATE TABLE CoastalErosionRates (
    location_id SERIAL PRIMARY KEY,
    location_name VARCHAR(100) NOT NULL,
    erosion_rate DECIMAL NOT NULL,
    date_recorded DATE
);

CREATE TABLE CoastalProtectionMeasures (
    measure_id SERIAL PRIMARY KEY,
    measure_name VARCHAR(100) NOT NULL,
    effectiveness DECIMAL NOT NULL,
    cost DECIMAL NOT NULL
);

CREATE TABLE VulnerableCoastalAreas (
    area_id SERIAL PRIMARY KEY,
    area_name VARCHAR(100) NOT NULL,
    threat_level VARCHAR(50) NOT NULL
);

CREATE TABLE ErosionMeasurementLogs (
    log_id SERIAL PRIMARY KEY,
    location_id INT NOT NULL,
    measurement_date DATE NOT NULL,
    erosion_depth DECIMAL NOT NULL
);

CREATE TABLE ProtectionMeasureLogs (
    log_id SERIAL PRIMARY KEY,
    measure_id INT NOT NULL,
    log_date DATE NOT NULL,
    success_rate DECIMAL NOT NULL
);
