tables.table_schema:
CREATE TABLE biodiversity_hotspots (
    hotspot_id INT PRIMARY KEY,
    hotspot_name VARCHAR(50),
    location VARCHAR(100),
    biodiversity_index FLOAT
);

CREATE TABLE conservation_efforts (
    effort_id INT PRIMARY KEY,
    hotspot_id INT,
    conservation_organization VARCHAR(100),
    conservation_status VARCHAR(50),
    FOREIGN KEY (hotspot_id) REFERENCES biodiversity_hotspots(hotspot_id)
);

CREATE TABLE MarineBiodiversityHotspots (
  hotspot_id INT PRIMARY KEY,
  hotspot_name VARCHAR(255),
  location_geometry GEOMETRY
);

CREATE TABLE Marine_Biodiversity_Hotspots (
	hotspot_id INT PRIMARY KEY,
	hotspot_name VARCHAR(255) NOT NULL,
	location VARCHAR(255) NOT NULL,
	biodiversity_index FLOAT
);

CREATE TABLE Conservation_Efforts (
	effort_id INT PRIMARY KEY,
	effort_name VARCHAR(255) NOT NULL,
	effort_type VARCHAR(50),
	effort_description TEXT
);

CREATE TABLE Habitats (
	habitat_id INT PRIMARY KEY,
	habitat_name VARCHAR(255) NOT NULL,
	habitat_type VARCHAR(50),
	habitat_description TEXT
);
