tables.table_schema:
CREATE TABLE compliance_monitoring (
    agency_id INT PRIMARY KEY,
    industry_id INT,
    renewable_energy_production DECIMAL,
    compliance_status VARCHAR
);

CREATE TABLE renewable_energy_compliance (
  id INT PRIMARY KEY,
  industry_id INT,
  compliance_date DATE
);

CREATE TABLE government_agencies (
  id INT PRIMARY KEY,
  agency_name VARCHAR(50),
  contact_person VARCHAR(50),
  contact_email VARCHAR(50)
);

CREATE TABLE industry_data (
  id INT PRIMARY KEY,
  industry_name VARCHAR(50),
  renewable_energy_capacity DECIMAL(10, 2)
);

CREATE TABLE Energy_Companies (
    company_id INT PRIMARY KEY,
    company_name VARCHAR(255),
    industry_type VARCHAR(100),
    renewable_energy_production DECIMAL
);

CREATE TABLE Renewable_Energy_Policies (
    policy_id INT PRIMARY KEY,
    policy_name VARCHAR(255),
    policy_description TEXT
);

CREATE TABLE Government_Agencies (
    agency_id INT PRIMARY KEY,
    agency_name VARCHAR(255),
    agency_type VARCHAR(100)
);
