tables.table_schema:
CREATE TABLE neighborhood_population_data (
  neighborhood_id INT PRIMARY KEY,
  neighborhood_name VARCHAR(255),
  population_count INT
);

CREATE TABLE neighborhoods ( 
    neighborhood_id INT PRIMARY KEY, 
    neighborhood_name VARCHAR(255), 
    population INT 
);

CREATE TABLE city_regions ( 
    region_id INT PRIMARY KEY, 
    region_name VARCHAR(255), 
    neighborhood_id INT, 
    FOREIGN KEY (neighborhood_id) REFERENCES neighborhoods(neighborhood_id) 
);

CREATE TABLE neighborhoods (
    neighborhood_id INT PRIMARY KEY,
    neighborhood_name VARCHAR(255) NOT NULL,
    population INT NOT NULL
);

CREATE TABLE households (
    household_id INT PRIMARY KEY,
    neighborhood_id INT NOT NULL,
    total_population INT NOT NULL
);

CREATE TABLE individuals (
    individual_id INT PRIMARY KEY,
    household_id INT NOT NULL,
    name VARCHAR(255) NOT NULL,
    age INT NOT NULL
);

CREATE TABLE neighborhoods (
    neighborhood_id UUID PRIMARY KEY,
    neighborhood_name VARCHAR(50) NOT NULL,
    population_count INT NOT NULL
);

CREATE TABLE demographic_data (
    demographic_id UUID PRIMARY KEY,
    neighborhood_id UUID,
    median_age INT,
    avg_income DECIMAL(10, 2)
);

CREATE TABLE housing_data (
    housing_id UUID PRIMARY KEY,
    neighborhood_id UUID,
    num_houses INT,
    avg_house_price DECIMAL(10, 2)
);

CREATE TABLE amenities_data (
    amenity_id UUID PRIMARY KEY,
    neighborhood_id UUID,
    num_parks INT,
    num_restaurants INT
);
